    CONSTRAINT "PK_PlaceVendues" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_PlaceVendues_Evenements_EvenementId" FOREIGN KEY ("EvenementId") REFERENCES "Evenements" ("Id") ON DELETE CASCADE,
    CONSTRAINT "FK_PlaceVendues_TypePlaces_TypePlaceId" FOREIGN KEY ("TypePlaceId") REFERENCES "TypePlaces" ("Id") ON DELETE CASCADE,
    CONSTRAINT "FK_PlaceVendues_Utilisateurs_UtilisateurId" FOREIGN KEY ("UtilisateurId") REFERENCES "Utilisateurs" ("Id") ON DELETE CASCADE
);

